import React, { useEffect, useState } from 'react';
import EventIcon from '@mui/icons-material/Event';
import Input from './Input';
import { Avatar, Button, Paper, Grid, Typography, Container } from '@mui/material';
import useStyles from './styles';
import Dropdown from './Dropdown';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate, useParams } from 'react-router-dom'
import { getSubjects } from '../../actions/subjects';
import { getEventTypes } from '../../actions/eventTypes';
import { createEvent, editEvent, deleteEvent, getEvent } from '../../actions/events';


function Form() {

  const classes = useStyles();
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { eventId } = useParams();
  const initialState = { eventId: null, title: '', subject: '', start: '', duration: '', eventType: '', repeatNum: 0, repeatTxt: '', repeatEnd: '' };
  const [form, setForm] = useState(initialState);
  const isAdd = eventId == null;
  const subjects = useSelector((state) => state.subjects);
  const eventTypes = useSelector((state) => state.eventTypes);

  const repeatText = [
    { id: 'days', name: 'days' },
    { id: 'weeks', name: 'weeks' },
    { id: 'months', name: 'months' }
  ];

  useEffect(() => {
    dispatch(getSubjects());
  }, [dispatch]);

  useEffect(() => {
    dispatch(getEventTypes());
  }, [dispatch]);

  useEffect(() => {
    if (!isAdd) {
      dispatch(getEvent(eventId));
    }
  }, [dispatch]);

  const handleSubmit = (e) => {
    e.preventDefault()

    if (isAdd) {
      dispatch(createEvent(form, navigate));
    } else {
      dispatch(editEvent(form, navigate));
    }
  };

  const handleDelete = (e) => {
    e.preventDefault();

    dispatch(deleteEvent(form, navigate));
  }

  const handleChange = (e) => {
    const name = e.target.name;
    const value = e.target.value;

    if (name === 'duration' || name === 'repeatNum') {
      setForm({ ...form, [name]: parseInt(value) });

    } else if (name === 'start') {
      const newValue = value.replace(/T/gi, ' ');

      setForm({ ...form, [name]: newValue });

    } else {
      setForm({ ...form, [name]: value });
    }
  }

  return (
    <Container component="main" maxWidth="sm">
      <Paper className={classes.paper} elevation={3}>
        <Avatar className={classes.avatar}>
          <EventIcon />
        </Avatar>
        <Typography component="h1" variant="h5">{isAdd ? 'Add event' : 'Edit event'}</Typography>
        <form className={classes.form} onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            <Input name="title" label="Title" handleChange={handleChange} required={true} type={'text'} />
            <Dropdown
              id="subject-select"
              idLabel="subject-select-label"
              name="subject"
              label="Subject" handleChangeForm={handleChange}
              items={subjects} required={true}
            />
            <Input
              name="start"
              label="Start time"
              handleChange={handleChange}
              required={true}
              type={'datetime-local'}
              shrink={true}
            />
            <Input
              name="duration"
              label="Duration in hours"
              handleChange={handleChange}
              required={true}
              type={'number'}
            />
            <Dropdown
              id="eventType-select"
              idLabel="eventType-select-label"
              name="eventType"
              label="Event type"
              handleChangeForm={handleChange}
              items={eventTypes}
              required={true}
            />
            <Input
              name="repeatNum"
              label="Repeat num."
              handleChange={handleChange}
              type={'number'}
            />
            {form.repeatNum ? (
              <>
                <Dropdown
                  id="repeat-select"
                  idLabel="repeat-select-label"
                  name="repeatTxt"
                  label="Repeat period"
                  handleChange={handleChange}
                  items={repeatText}
                  required={form.repeat_num ? true : false}
                />
                <Input
                  name="repeatEnd"
                  label="End date"
                  handleChange={handleChange}
                  required={form.repeat_num ? true : false}
                  type={'datetime-local'}
                  shrink={true}
                />
              </>
            ) : (
              <></>
            )}
          </Grid>
          <Grid container justifyContent="center">
            {!isAdd ? (
              <Grid item xs={8}>
                <Button className={classes.submit} onClick={handleDelete}>Delete</Button>
              </Grid>
            ) : (
              <></>
            )}
            <Grid item xs={isAdd ? 12 : 4}>
              <Button onClick={handleSubmit} fullWidth variant="contained" color="primary" className={classes.submit}>Save</Button>
            </Grid>
          </Grid>
        </form>
      </Paper>
    </Container >
  )
}

export default Form;
