import React, { useState } from 'react';
import { TextField, Grid, Button } from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';

function Filter({ handleFilter, user }) {
  const initialState = { start: '', end: '' };
  const [form, setForm] = useState(initialState);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  return (
    user ? (
      <form >
        <Grid container columnSpacing={2} sx={{ marginTop: 5, marginBottom: 7 }}>
          <Grid item >
            <TextField type='datetime-local' onChange={handleChange} name="start" label="Start" variant="outlined" shrink />
          </Grid>
          <Grid item>
            <TextField type='datetime-local' onChange={handleChange} name="end" label="End" variant="outlined" shrink />
          </Grid>
          <Grid item >
            <Button onSubmit={handleFilter} variant='contained' sx={{ height: 55 }}>
              <SearchIcon />
            </Button>
          </Grid>
        </Grid>
      </form>
    ) : (<></>)
  );
}

export default Filter;
