import React, { useEffect, useState } from 'react';
import EventIcon from '@mui/icons-material/Event';
import Input from './Input';
import { Avatar, Button, Paper, Grid, Typography, Container } from '@mui/material';
import useStyles from './styles';
import Dropdown from './Dropdown';
import { useDispatch } from 'react-redux';
import { useSelector } from 'react-redux';
import { getSubjects } from '../../actions/subjects';
import { getEventTypes } from '../../actions/eventTypes';


function Form({ event }) {

  const classes = useStyles();
  const dispatch = useDispatch();
  const initialState = { id: '', title: '', subject: '', start_time: '', duration: '', event_type: '', repeat_num: '', repeat_txt: '', end_repeat: '' };
  const [form, setForm] = useState(initialState);
  const isAdd = event == null;
  const subjects = useSelector((state) => state.subjects);
  const eventTypes = useSelector((state) => state.eventTypes);

  const repeatText = [
    { id: 'days', name: 'days' },
    { id: 'weeks', name: 'weeks' },
    { id: 'months', name: 'months' }
  ];

  useEffect(() => {
    dispatch(getSubjects());
  }, [dispatch]);

  useEffect(() => {
    dispatch(getEventTypes());
  }, [dispatch]);

  const handleSubmit = (e) => {
    e.preventDefault();

    alert('Submit')
  };

  const handleDelete = (e) => {
    e.preventDefault();

    // TODO dispatch to /events/delete
  }

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  return (
    <Container component="main" maxWidth="sm">
      <Paper className={classes.paper} elevation={3}>
        <Avatar className={classes.avatar}>
          <EventIcon />
        </Avatar>
        <Typography component="h1" variant="h5">{isAdd ? 'Add event' : 'Edit event'}</Typography>
        <form className={classes.form} onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            <Input name="title" label="Title" handleChange={handleChange} required={true} type={'text'} />
            <Dropdown name="subject" label="Subject" handleChangeForm={handleChange} items={subjects} required={true} />
            <Input name="start_time" label="Start time" handleChange={handleChange} required={true} type={'date'} shrink={true} />
            <Input name="duration" label="Duration in hours" handleChange={handleChange} required={true} type={'number'} />
            <Dropdown name="event_type" label="Event type" handleChange={handleChange} items={eventTypes} required={true} />
            <Input name="repeat_num" label="Repeat num." handleChange={handleChange} type={'number'} />
            {form.repeat_num ? (
              <>
                <Dropdown name="repeat_txt" label="Repeat period" handleChange={handleChange} items={repeatText} required={form.repeat_num ? true : false} />
                <Input name="end_repeat" label="End date" handleChange={handleChange} required={form.repeat_num ? true : false} type={'date'} shrink={true} />
              </>
            ) : (
              <></>
            )}
          </Grid>
          <Grid container justifyContent="center">
            {!isAdd ? (
              <Grid item xs={8}>
                <Button className={classes.submit} onClick={handleDelete}>Delete</Button>
              </Grid>
            ) : (
              <></>
            )}
            <Grid item xs={isAdd ? 12 : 4}>
              <Button onClick={handleSubmit} fullWidth variant="contained" color="primary" className={classes.submit}>Save</Button>
            </Grid>
          </Grid>
        </form>
      </Paper>
    </Container >
  )
}

export default Form;
