import * as actionType from '../constants/actionTypes';

export default (events = [], action) => {
    switch (action.type) {
        case actionType.GET_EVENTS:
        case actionType.GET_EVENT:
            return action.payload;
        case actionType.CREATE_EVENT:
            return [...events, action.payload];
        case actionType.DELETE_EVENT:
            return events.filter((event) => event.id !== action.payload.id);
        case actionType.EDIT_EVENT:
            return events.map((event) => (event.id === action.payload.id ? action.payload : event));
        default:
            return events;
    }
};
