import React, { useState } from 'react';
import EventIcon from '@mui/icons-material/Event';
import Input from './Input';
import { Avatar, Button, Paper, Grid, Typography, Container } from '@mui/material';
import useStyles from './styles';
import Dropdown from './Dropdown';
import { useSelector } from 'react-redux';

function Form({ event }) {

  const classes = useStyles();
  const initialState = { id: '', title: '', subject: '', start_time: '', duration: '', event_type: '', repeat_num: '', repeat_txt: '', end_repeat: '' };
  const [form, setForm] = useState(initialState);
  const isAdd = event == null;
  const subjects = useSelector((state) => state.subjects);
  const eventTypes = useSelector((state) => state.eventTypes);


  const handleSubmit = (e) => {
    e.preventDefault();
  };

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const repeatText = [
    { id: 'days', name: 'days' },
    { id: 'weeks', name: 'weeks' },
    { id: 'months', name: 'months' }
  ];

  return (
    <Container component="main" maxWidth="sm">
      <Paper className={classes.paper} elevation={3}>
        <Avatar className={classes.avatar}>
          <EventIcon />
        </Avatar>
        <Typography component="h1" variant="h5">{isAdd ? 'Add event' : 'Edit event'}</Typography>
        <form className={classes.form} onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            <Input name="title" label="Title" handleChange={handleChange} required={true} />
            <Dropdown name="subject" label="Subject" handleChangeForm={handleChange} items={[]} required={true} />
            <Input name="start_time" label="Start time" handleChange={handleChange} required={true} />
            <Input name="duration" label="Duration in hours" handleChange={handleChange} required={true} />
            <Dropdown name="event_type" label="Event type" handleChange={handleChange} items={[]} required={true} />
            <Input name="repeat_num" label="Repeat num." handleChange={handleChange} />
            {form.repeat_num ? (
              <>
                <Dropdown name="repeat_txt" label="Repeat period" handleChange={handleChange} items={repeatText} required={form.repeat_num ? true : false} />
                <Input name="end_repeat" label="End date" handleChange={handleChange} required={form.repeat_num ? true : false} />
              </>
            ) : (
              <></>
            )}
          </Grid>
          <Grid container justifyContent="center">
            {!isAdd ? (
              <Grid item xs={8}>
                <Button className={classes.submit}>Delete</Button>
              </Grid>
            ) : (
              <></>
            )}
            <Grid item xs={isAdd ? 12 : 4}>
              <Button type="submit" fullWidth variant="contained" color="primary" className={classes.submit}>Save</Button>
            </Grid>
          </Grid>
        </form>
      </Paper>
    </Container >
  )
}

export default Form;
