import React, { useEffect, useState } from 'react';
import Calendar from 'react-awesome-calendar';
import useStyles from './styles';
import { Paper, Box, SpeedDial, SpeedDialAction } from '@mui/material';
import SpeedDialIcon from '@mui/material/SpeedDialIcon';
import { styled } from '@mui/material/styles';
import EventIcon from '@mui/icons-material/Event';
import { useDispatch } from 'react-redux';
import { getEvents } from '../../actions/events';
import { useSelector } from 'react-redux';

function Home() {
  const classes = useStyles();
  const dispatch = useDispatch();
  const [eventsStore, setEventsStore] = useState([]);
  const user = useState(JSON.parse(localStorage.getItem('profile')));
  const events = useSelector((state) => state.eventsStore);

  const StyledSpeedDial = styled(SpeedDial)(({ theme }) => ({
    position: 'absolute',
    '&.MuiSpeedDial-directionUp, &.MuiSpeedDial-directionLeft': {
      bottom: theme.spacing(2),
      right: theme.spacing(2),
    }
  }));

  const actions = [
    { icon: <EventIcon />, name: 'Add new event' }
  ];

  useEffect(() => {
    if (user) {
      dispatch(getEvents());
    }
  }, [eventsStore, dispatch]);

  console.log(state);

  return (
    <Paper className={classes.paper} elevation={3}>
      <Box sx={{ position: 'relative', mt: 3, height: 80 }}>
        <StyledSpeedDial
          ariaLabel="SpeedDial"
          icon={<SpeedDialIcon />}
          direction='left'
        >
          {actions.map((action) => (
            <SpeedDialAction
              key={action.name}
              icon={action.icon}
              tooltipTitle={action.name}
              onClick={() => alert("Add new event!")}
            />
          ))}
        </StyledSpeedDial>
      </Box>

      <Calendar events={events} />
    </Paper>
  );
}

export default Home;
