import React, { useState } from 'react';
import EventIcon from '@mui/icons-material/Event';
import Input from './Input';
import { Avatar, Button, Paper, Grid, Typography, Container } from '@mui/material';
import useStyles from './styles';

function Form() {

  const classes = useStyles();
  const initialState = { id: '', title: '', subject: '', start_time: '', duration: '', event_type: '', repeat_num: '', repeat_txt: '', end_repeat: '' };
  const [form, setForm] = useState(initialState);
  const [isAdd, setIsAdd] = useState(true);

  const switchMode = () => {
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  return (
    <Container component="main" maxWidth="xs">
      <Paper className={classes.paper} elevation={3}>
        <Avatar className={classes.avatar}>
          <EventIcon />
        </Avatar>
        <Typography component="h1" variant="h5">{isAdd ? 'Add event' : 'Edit event'}</Typography>
        <form className={classes.form} onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            <Input name="title" label="Title" handleChange={handleChange} />
            <Input name="subject" label="Subject" handleChange={handleChange} />
            <Input name="start_time" label="Start time" handleChange={handleChange} />
            <Input name="duration" label="Duration in hours" handleChange={handleChange} />
            <Input name="event_type" label="Event type" handleChange={handleChange} />
          </Grid>
          <Grid container justifyContent="flex">
            <Grid item>
              <Button>Delete</Button>
            </Grid>
            <Grid item>
              <Button type="submit" fullWidth variant="contained" color="primary" className={classes.submit}>Save</Button>
            </Grid>
          </Grid>
        </form>
      </Paper>
    </Container>
  )
}

export default Form;
