import React from 'react';
import { TextField, Grid } from '@mui/material'

const Input = ({ name, handleChange, label, autoFocus, type, required, shrink, value }) => (
  <Grid item xs={12} sm={12}>
    <TextField
      name={name}
      onChange={handleChange}
      variant="outlined"
      required={required}
      fullWidth
      label={label}
      autoFocus={autoFocus}
      type={type}
      value={value}
      InputLabelProps={{
        shrink: shrink,
      }}
      InputProps={name === 'duration' ? { inputProps: { min: 1 } } : null}
      InputProps={name === 'repeatNum' ? { inputProps: { min: 0 } } : null}
    />
  </Grid>
);

export default Input;
